var config = require('config');
var log4js = require('log4js');
log4js.configure(config.log4js);
var node_env = process.env.NODE_EVN || 'development';
var logger = config.log4js ? log4js.getLogger('console') : log4js.getLogger('logFile');

module.exports = {
    log4js: log4js,
    getLogger: function () {
        return logger;
    }
};